import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getAllUsers } from "src/api/apiUser";
import DataTable from "../base/tables/User_Manage_DataTable";

export default function Manage_user() {
  const [users, setUsers] = useState([]);

  useEffect(()=>{
    getAllUsers()
      .then(response => setUsers(response.data))
      .catch(err => alert("Something went wrong!"))
  },[])


  return (
    <div>
      <Link className="btn btn-info mb-2" to="/user/add_user" >Add User</Link>
      <table>
        <thead>
          <th>User Name</th>
          <th>Email</th>
          <th>Name</th>
          <th>Phone</th>
          <th>Address</th>
          <th>Group</th>
        </thead>

        <tbody>
          {users && users.map((user,i) => {
           <tr>
              <td>email: {alert(user.email)}</td>
              <td>email: {user.email}</td>
              <td>email: {user.email}</td>
              <td>email: {user.email}</td>
              <td>email: {user.email}</td>
              <td>email: {user.email}</td>
            </tr>
          })}
         
        </tbody>
      </table>
    </div>
  );
}
