import React from "react";
// import { Dialog } from "@material-ui/core";
import { useState } from "react";
import { Modal, Button } from "react-bootstrap";
import { deletebuyphaseById, getbuyphaseById, updatebuyphaseById } from "src/api/apiBuyPhase";


const BuyPhaseTable = ({item}) => {
    const [show, setShow] = useState(false);
    const [redirect, setRedirect] = useState(false); 
    const [buyphasebyid, setbuyphasebyid] = useState({
        id: '',
        Phaseno: '',
        tokenNo: '',
        price: '',
        tax: '',
        min_buy: '',
        max_buy: '',
        day: '',
        month: '',
        year: '',
        hour: '',
        min: '',
        sec: ''
        });

    const {
        id,
        Phaseno,
        tokenNo,
        price,
        tax,
        min_buy,
        max_buy,
        day,
        month,
        year,
        hour,
        min,
        sec
    } = buyphasebyid;

    const handleClose = () => setShow(false);
    const handleShow = (id) =>{
        //alert(id)
        getbuyphaseById(id)
        .then(response => setbuyphasebyid(response.data))
        .catch(err => alert("something went wrong"))
        setShow(true);
    }

    const handleDelete = (id) => {
        deletebuyphaseById(id)
        .then(
            alert(" Deleted"),
            window.location.reload()
        )
        .catch(err=> alert("Something Went Wrong!"))
    }
    const handleChange = (e) => {
        alert(e.target.name);
        setbuyphasebyid({
           ...buyphasebyid,
           [e.target.name]: e.target.value
       }
       )
    }

    const handleSubmit = (e) => {
        updatebuyphaseById(buyphasebyid, id)
            .then(response => {
                if(response.status === 200){
                    alert("updated");
                    setRedirect(true)
                }
            })
    }

    const profileForm = ()  => (
        <form onSubmit={handleSubmit}>
        <label>Phase No</label>
        <select name="Phaseno" value={Phaseno} onChange={handleChange}>
            <option value="">Phaseno</option> 
            <option value="1">1</option>
            <option value="2">2</option>
        </select>

        <input
            type="text"
            name="Phaseno"
            value={Phaseno}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>Token</label>
        <input
            type="text"
            name="tokenNo"
            value={tokenNo}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>price</label>
        <input
            type="text"
            name="price"
            value={price}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>tax</label>
        <input
            type="text"
            name="tax"
            value={tax}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>min buy</label>
        <input
            type="text"
            name="min_buy"
            value={min_buy}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>max_buy</label>
        <input
            type="text"
            name="max_buy"
            value={max_buy}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>day</label>
        <input
            type="text"
            name="day"
            value={day}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>month</label>
        <input
            type="text"
            name="month"
            value={month}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>year</label>
        <input
            type="text"
            name="year"
            value={year}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>hour</label>
        <input
            type="text"
            name="hour"
            value={hour}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>min</label>
        <input
            type="text"
            name="min"
            value={min}
            className="form-control"
            onChange={handleChange}
            required/>

        <label>sec</label>
        <input
            type="text"
            name="sec"
            value={sec}
            className="form-control"
            onChange={handleChange}
            required/>


    </form>   
    )

    return (
        <>

              <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Fill Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {profileForm()}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
      {redirect ? window.location.replace("/buyphase"): ""}
        <tr>
            <td>{item.Phaseno}</td>
            <td>{item.tokenNo}</td>

            <td>{item.price}</td>
            <td>{item.tax}</td>
            <td>{item.min_buy}</td>
            <td>{item.max_buy}</td>
            <td>{item.day}/{item.month}/{item.year}; 
            {item.hour}:{item.min}:{item.sec}
            </td>

