import axios from "axios";
import { API } from "../utils/config";

export const insertBrand = (data) => {
    //alert(`${Object.keys (data)} = ${Object.values(data)}`);
        return axios.post(`${API}/create/brand`, data, {
        headers: {
            "Content-Type": "application/json",
        }
    })
}

export const getAllBrands = () => {
    return axios.get(`${API}/getall/brand`)
}


export const getBrandsById = (id) => {
    return axios.get(`${API}/brand/${id}`)
}

export const updateBrandsById = (data,id) => {
    return axios.put(`${API}/update/brand/${id}`, data, {
        headers: {
            "Content-Type": "application/json"
        }
    })
}

export const deleteBrandById = (id) => {
    return axios.delete(`${API}/brand/${id}`)
}
